// AirportDropdown.js

import React from 'react';

const AirportDropdown = () => {
  const airports = [
    { id: 1, name: 'SeaTac' },
    { id: 2, name: 'Airport 2' },
    { id: 3, name: 'Airport 3' },
    // Add more airports as needed
  ];

  const handleChange = (e) => {
    // Handle selected airport change if needed
    const selectedAirportId = e.target.value;
    console.log('Selected Airport ID:', selectedAirportId);
    // Perform any actions based on the selected airport
  };

  const handleFlightInputChange = (e) => {
    // Handle changes in the flight input textbox
    const flightNumber = e.target.value;
    console.log('Flight Number:', flightNumber);
    // Perform any actions based on the flight number input
  };

  return (
    <div className="airport-selector" style={{ marginTop: '50px', marginBottom: '20px', marginLeft: '10px', marginRight: '10px' }}>
      <label htmlFor="airportDropdown">Choosing Airport:</label>
      <div className="custom-dropdown" style={{ padding: '10px' }}>
        <select id="airportDropdown" onChange={handleChange}>
          <option value="">Select an airport</option>
          {airports.map((airport) => (
            <option key={airport.id} value={airport.id}>
              {airport.name}
            </option>
          ))}
        </select>
      </div>
      <input
        type="text"
        placeholder="Enter flight number"
        onChange={handleFlightInputChange}
        style={{ marginTop: '10px', padding: '8px' }}
      />
    </div>
  );
};

export default AirportDropdown;
