import React, { useState } from 'react';
import axios from 'axios';

const LocationComponent = () => {
  const [location, setLocation] = useState(null);
  const [buttonVisible, setButtonVisible] = useState(true);

  const handleGetLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setLocation({ latitude, longitude });
          setButtonVisible(false); // Hide the button after obtaining the location

          // Send the obtained location to your backend
          sendLocationToBackend({ latitude, longitude });

        },
        (error) => {
          console.error('Error getting location:', error);
          // Handle errors such as user denying location access
        }
      );
    } else {
      console.error('Geolocation is not supported by this browser');
      // Handle cases where geolocation is not supported
    }
  };

    // Function to send the location to the backend
    const sendLocationToBackend = (coordinates) => {
      const backendApiUrl = 'http://localhost:5000/updateDepartureCoordinate'; // Replace with your actual backend endpoint
  
      // Create a JavaScript object to send as the request body
      const requestData = {
        departureCoordinate: `${coordinates.latitude},${coordinates.longitude}`,
      };
  
      // Make a POST request to the backend API
      axios.post(backendApiUrl, requestData)
        .then(response => {
          if (response.status === 200) {
            // The request was successful
            console.log('User departure coordinate sent to the backend successfully.');
          } else {
            // Handle other response statuses (e.g., error)
            console.error(`HTTP error! Status: ${response.status}`);
          }
        })
        .catch(error => {
          // Handle network errors or other issues
          console.error('Error sending user departure coordinate:', error);
        });
    };

  return (
    <div className="location-selector" style={{ marginTop: '50px'}}>
      {buttonVisible && <button className="button" onClick={handleGetLocation} >Get My Location</button>}
      {location && (
        <div style={{ marginTop: '30px' }}>
          <p>Starting Location: {location.latitude}, {location.longitude}</p>
        </div>
      )}
    </div>
  );
  
};

export default LocationComponent;
